css:
.signs-cont{
	width:100%;display:block;clear:both;padding-top: 12px;
}
.apvdby{
	width:132px;
	margin:	auto;
	text-align: center;
    border-top: 2px solid #000;
	padding-top: 5px;
	font-weight: bold;
    text-transform: uppercase;
}
.signcont{
	width:162px;float:left;height:140px;display:block;
}
.sigimgcont{
	display:block;height:120px;width:100%;padding-top: 12px;
}

@media print {
  .signs-cont{page-break-inside: avoid;}
   div.signs-cont {break-inside: avoid;  }

}

